sys_script_include.script:
var getPreviousBusinessDate = function(daysPrevious) {
	daysPrevious = daysPrevious < 0 ? 0 : daysPrevious; 
    //var sched = new GlideSchedule('090eecae0a0a0b260077e1dfa71da828', 'US/Eastern');
	var sched = new GlideSchedule('090eecae0a0a0b260077e1dfa71da828');
    var startDayTime = '08:00:00';
    var currentDate = new GlideDate();
    var dur = new GlideDuration();
	//+1 to days previous so the current day is not counted; 9 hours in a business day per schedule
    dur.setValue(60 * 60 * 9 * 1000 * (daysPrevious + 1));

    var daysToGoBack = daysPrevious * -1;
    do{
		var targetDate = new GlideDateTime(currentDate.getDisplayValue() + ' ' + startDayTime);
        targetDate.addDaysLocalTime(daysToGoBack);
		//adding duration to target date to see if we went back far enough.  if test date
		// is greater than current date, we did not take enough days off to account for weekend/holiday
        var testDate = sched.add(targetDate, dur);
		daysToGoBack-=1;
    //}while(!(new GlideDateTime(testDate.getDate().getDisplayValue()).equals(new GlideDateTime(currentDate.getDisplayValue()))));
	}while((new GlideDateTime(testDate.getDate().getDisplayValue()) > (new GlideDateTime(currentDate.getDisplayValue()))));
	return targetDate;
};
